from langflow.custom.custom_component.component import Component
from langflow.io import BoolInput, DropdownInput, IntInput, MessageInput, MessageTextInput, Output
from langflow.schema.message import Message


class ConditionalRouterComponent(Component):
    display_name = "Multi-ConditionalRouter"
    description = "Routes an input message to a corresponding output based on text comparison."
    icon = "split"
    name = "ConditionalRouter"

    def __init__(self, *args, **kwargs):
        super().__init__(*args, **kwargs)
        self.__iteration_updated = False

    inputs = [
        MessageTextInput(
            name="input_text",
            display_name="Text Input",
            info="The primary text input for the operation.",
            required=True,
        ),
        MessageTextInput(
            name="match_text1",
            display_name="Match Text for Choice 1",
            info="If input text matches this text, route to Choice 1.",
        ),
        MessageTextInput(
            name="match_text2",
            display_name="Match Text for Choice 2",
            info="If input text matches this text, route to Choice 2.",
        ),

        MessageTextInput(
            name="match_text3",
            display_name="Match Text for Choice 3",
            info="If input text matches this text, route to Choice 3.",
        ),

        MessageTextInput(
            name="match_text4",
            display_name="Match Text for Choice 4",
            info="If input text matches this text, route to Choice 4.",
        ),

        MessageInput(
            name="message",
            display_name="Message",
            info="The message to pass through either route.",
            required=True,
        ),
    ]

    outputs = [
        Output(display_name="Choice 1", name="choice1", method="choice1_response", group_outputs=True),
        Output(display_name="Choice 2", name="choice2", method="choice2_response", group_outputs=True),
        Output(display_name="Choice 3", name="choice3", method="choice3_response", group_outputs=True),
        Output(display_name="Choice 4", name="choice4", method="choice4_response", group_outputs=True),

    ]

    def _pre_run_setup(self):
        self.__iteration_updated = False

    def evaluate_condition(self, input_text: str, match_text: str) -> bool:
        input_text = input_text.lower()
        match_text = match_text.lower()
        return input_text == match_text


    def iterate_and_stop_once(self, active_route: str):
        if not self.__iteration_updated:
            self.__iteration_updated = True
            routes_to_stop = ["choice1", "choice2", "choice3", "choice4"]
            routes_to_stop.remove(active_route)
            for route in routes_to_stop:
                self.stop(route)

    def stop_self(self, active_route: str):
        """Stops all routes except the active one"""
        self.stop(active_route)

    def is_right_response(self, choice: str, match_text: str) -> bool:
        result = self.evaluate_condition(self.input_text, match_text)
        if result:
            self.iterate_and_stop_once(choice)
            return True
        else:
            self.stop_self(choice)
            return False


    def choice1_response(self) -> Message:
        if self.is_right_response("choice1", self.match_text1):
            self.status = self.message
            return self.message
        return Message(content="")


    def choice2_response(self) -> Message:
        if self.is_right_response("choice2", self.match_text2):
            self.status = self.message
            return self.message
        return Message(content="")

    def choice3_response(self) -> Message:
        if self.is_right_response("choice3", self.match_text3):
            self.status = self.message
            return self.message
        return Message(content="")

    def choice4_response(self) -> Message:
        if self.is_right_response("choice4", self.match_text4):
            self.status = self.message
            return self.message
        return Message(content="")
